// O 'items' pode estar sublinhado, mas vai funcionar. Ele cont√©m a lista completa.
const todosOsAtivos = items;

// Ordena a lista pela varia√ß√£o, do menor (maior queda) para o maior (maior alta).
todosOsAtivos.sort((a, b) => {
  const varA = parseFloat(a.json.variacaoPercentual);
  const varB = parseFloat(b.json.variacaoPercentual);
  return varA - varB;
});

// --- Monta o relat√≥rio ---
let relatorio = "<h1>üìà Relat√≥rio Di√°rio de Ativos</h1>";

// --- Destaques ---
relatorio += "<h2>‚≠ê Destaques do Dia ‚≠ê</h2>";

const maioresQuedas = todosOsAtivos.slice(0, 2);
relatorio += "<h3>Maiores Quedas:</h3>";
for (const item of maioresQuedas) {
  const ativo = item.json.codigo;
  const variacao = parseFloat(item.json.variacaoPercentual).toFixed(2).replace('.', ',');
  // A MUDAN√áA EST√Å AQUI: Adicionamos o item.json.precoAtual
  const preco = parseFloat(item.json.precoAtual).toFixed(2).replace('.', ',');
  relatorio += `<p>üî¥ <b>${ativo}:</b> R$ ${preco} (${variacao}%)</p>`;
}

const maioresAltas = todosOsAtivos.slice(-2).reverse();
relatorio += "<h3>Maiores Altas:</h3>";
for (const item of maioresAltas) {
  const ativo = item.json.codigo;
  const variacao = parseFloat(item.json.variacaoPercentual).toFixed(2).replace('.', ',');
  // A MUDAN√áA EST√Å AQUI: Adicionamos o item.json.precoAtual
  const preco = parseFloat(item.json.precoAtual).toFixed(2).replace('.', ',');
  relatorio += `<p>üü¢ <b>${ativo}:</b> R$ ${preco} (${variacao}%)</p>`;
}

// --- Lista Completa ---
relatorio += "<hr><h2>Varia√ß√£o de todos os ativos:</h2>";

for (const item of todosOsAtivos) {
  const ativo = item.json.codigo;
  const variacao = parseFloat(item.json.variacaoPercentual).toFixed(2).replace('.', ',');
  const emoji = parseFloat(item.json.variacaoPercentual) >= 0 ? 'üîº' : 'üîΩ';
  // A MUDAN√áA EST√Å AQUI: Adicionamos o item.json.precoAtual
  const preco = parseFloat(item.json.precoAtual).toFixed(2).replace('.', ',');

  relatorio += `<p>${emoji} <b>${ativo}:</b> R$ ${preco} (${variacao}%)</p>`;
}

// Prepara o item final para ser enviado.
return [{ 
  json: {
    relatorioCompletoHTML: relatorio
  }
}];
